import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Search, 
  Plus, 
  User, 
  ChevronDown,
  Truck,
  Package,
  LogOut,
  Settings
} from 'lucide-react';

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userType, setUserType] = useState(''); // 'driver' or 'client'
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleProfileClick = () => {
    if (isLoggedIn) {
      setIsProfileOpen(!isProfileOpen);
    } else {
      // If not logged in, show login options
      setIsProfileOpen(!isProfileOpen);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserType('');
    setIsProfileOpen(false);
    navigate('/');
  };

  const handleLogin = () => {
    setIsProfileOpen(false);
    navigate('/login');
  };

  const handleSignup = () => {
    setIsProfileOpen(false);
    navigate('/signup');
  };

  const handleDashboard = () => {
    setIsProfileOpen(false);
    if (userType === 'driver') {
      navigate('/driver-dashboard');
    } else {
      navigate('/client-dashboard');
    }
  };

  return (
    <nav className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled ? 'bg-white shadow-lg' : 'bg-white'
    }`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2">
            <div className="flex items-center space-x-1">
              <div className="w-8 h-8 bg-cyan-400 rounded-full"></div>
              <div className="w-8 h-8 bg-primary-green rounded-full -ml-2"></div>
            </div>
            <span className="text-2xl font-bold text-primary-green">TranspoLink</span>
          </Link>

          {/* Right Side Navigation */}
          <div className="flex items-center space-x-6">
            {/* Search */}
            <button className="flex items-center space-x-2 text-cyan-500 hover:text-cyan-600 transition-colors duration-200">
              <Search className="w-5 h-5" />
              <span className="font-medium">Search</span>
            </button>

            {/* Publish */}
            <Link 
              to="/post-goods" 
              className="flex items-center space-x-2 text-cyan-500 hover:text-cyan-600 transition-colors duration-200"
            >
              <div className="w-6 h-6 bg-cyan-500 rounded-full flex items-center justify-center">
                <Plus className="w-4 h-4 text-white" />
              </div>
              <span className="font-medium">Publish a route</span>
            </Link>

            {/* User Profile */}
            <div className="relative">
              <button
                onClick={handleProfileClick}
                className="flex items-center space-x-2 text-cyan-500 hover:text-cyan-600 transition-colors duration-200"
              >
                <div className="w-8 h-8 bg-cyan-500 rounded-full flex items-center justify-center">
                  <User className="w-5 h-5 text-white" />
                </div>
                <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${
                  isProfileOpen ? 'rotate-180' : ''
                }`} />
              </button>

              {/* Profile Dropdown */}
              <AnimatePresence>
                {isProfileOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: -10, scale: 0.95 }}
                    transition={{ duration: 0.2 }}
                    className="absolute right-0 mt-2 w-64 bg-white rounded-lg shadow-xl border border-gray-200 py-2 z-50"
                  >
                    {isLoggedIn ? (
                      <>
                        {/* User Info */}
                        <div className="px-4 py-3 border-b border-gray-100">
                          <div className="flex items-center space-x-3">
                            <div className="w-10 h-10 bg-primary-green rounded-full flex items-center justify-center">
                              <User className="w-6 h-6 text-white" />
                            </div>
                            <div>
                              <p className="font-medium text-gray-900">John Doe</p>
                              <p className="text-sm text-gray-500 capitalize">{userType}</p>
                            </div>
                          </div>
                        </div>

                        {/* Dashboard */}
                        <button
                          onClick={handleDashboard}
                          className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-200 flex items-center space-x-3"
                        >
                          {userType === 'driver' ? (
                            <Truck className="w-5 h-5 text-primary-green" />
                          ) : (
                            <Package className="w-5 h-5 text-primary-green" />
                          )}
                          <span className="font-medium text-gray-700">
                            {userType === 'driver' ? 'Driver Dashboard' : 'Client Dashboard'}
                          </span>
                        </button>

                        {/* Settings */}
                        <button className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-200 flex items-center space-x-3">
                          <Settings className="w-5 h-5 text-gray-500" />
                          <span className="font-medium text-gray-700">Settings</span>
                        </button>

                        {/* Logout */}
                        <button
                          onClick={handleLogout}
                          className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-200 flex items-center space-x-3 border-t border-gray-100"
                        >
                          <LogOut className="w-5 h-5 text-red-500" />
                          <span className="font-medium text-red-500">Logout</span>
                        </button>
                      </>
                    ) : (
                      <>
                        {/* Login */}
                        <button
                          onClick={handleLogin}
                          className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-200 flex items-center space-x-3"
                        >
                          <User className="w-5 h-5 text-primary-green" />
                          <span className="font-medium text-gray-700">Login</span>
                        </button>

                        {/* Signup */}
                        <button
                          onClick={handleSignup}
                          className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-200 flex items-center space-x-3 border-t border-gray-100"
                        >
                          <Plus className="w-5 h-5 text-primary-green" />
                          <span className="font-medium text-gray-700">Sign Up</span>
                        </button>
                      </>
                    )}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
