import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  Truck, 
  Package, 
  MapPin, 
  Calendar, 
  User, 
  Search,
  Shield, 
  Star, 
  ArrowRight,
  CheckCircle,
  Clock,
  Globe
} from 'lucide-react';

const Home = () => {
  const [searchData, setSearchData] = useState({
    from: '',
    to: '',
    date: '',
    passengers: '1'
  });

  const handleSearchChange = (field, value) => {
    setSearchData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Handle search logic here
    console.log('Search:', searchData);
  };

  const features = [
    {
      icon: <Truck className="w-12 h-12" />,
      title: "Your pick of trucks at low prices",
      description: "No matter where you're going, find the perfect transport from our wide range of destinations and routes at competitive prices."
    },
    {
      icon: <Shield className="w-12 h-12" />,
      title: "Trust who you transport with",
      description: "We take the time to verify each of our drivers and partners. We check reviews, profiles and IDs, so you know who you're working with."
    },
    {
      icon: <Search className="w-12 h-12" />,
      title: "Search, book and go!",
      description: "Booking transport has never been easier! Thanks to our simple platform powered by great technology, you can book transport in just minutes."
    }
  ];

  const stats = [
    { number: "10,000+", label: "Verified Drivers", icon: <User className="w-6 h-6" /> },
    { number: "50,000+", label: "Successful Deliveries", icon: <CheckCircle className="w-6 h-6" /> },
    { number: "4.8", label: "Average Rating", icon: <Star className="w-6 h-6" /> },
    { number: "25+", label: "Cities Covered", icon: <Globe className="w-6 h-6" /> }
  ];

  return (
    <div className="pt-16">
      {/* Hero Section with Search Form */}
      <section className="relative min-h-screen flex items-center">
        {/* Background with transport illustration */}
        <div className="absolute inset-0 bg-gradient-to-br from-primary-green via-green-600 to-primary-orange">
          <div className="absolute inset-0 bg-black opacity-20"></div>
          
          {/* Transport illustration elements */}
          <div className="absolute top-20 left-10 w-32 h-32 bg-white/10 rounded-full blur-xl"></div>
          <div className="absolute top-40 right-20 w-24 h-24 bg-white/10 rounded-full blur-xl"></div>
          <div className="absolute bottom-40 left-1/4 w-20 h-20 bg-white/10 rounded-full blur-xl"></div>
          
          {/* Road/bridge illustration */}
          <div className="absolute bottom-1/3 left-0 right-0 h-2 bg-white/30 transform -skew-y-12"></div>
          
          {/* Truck illustrations */}
          <div className="absolute bottom-1/3 left-1/4 w-16 h-8 bg-white/80 rounded-lg transform -skew-y-12 flex items-center justify-center">
            <Truck className="w-8 h-6 text-primary-green" />
          </div>
          <div className="absolute bottom-1/3 left-1/2 w-16 h-8 bg-white/80 rounded-lg transform -skew-y-12 flex items-center justify-center">
            <Package className="w-8 h-6 text-primary-green" />
          </div>
          <div className="absolute bottom-1/3 right-1/4 w-16 h-8 bg-white/80 rounded-lg transform -skew-y-12 flex items-center justify-center">
            <Truck className="w-8 h-6 text-primary-green" />
          </div>
        </div>

        <div className="relative z-10 w-full">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {/* Hero Content */}
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              className="text-center text-white mb-16"
            >
              <h1 className="text-5xl md:text-7xl font-bold mb-6">
                Your pick of trucks at
                <span className="block text-primary-orange">low prices</span>
              </h1>
              <p className="text-xl text-gray-200 max-w-3xl mx-auto leading-relaxed">
                Connect with reliable transport services for your goods. 
                Safe, secure, and cost-effective transportation across the country.
              </p>
            </motion.div>

            {/* Search Form */}
            <motion.div
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="max-w-4xl mx-auto"
            >
              <form onSubmit={handleSearch} className="bg-white rounded-2xl shadow-2xl p-8">
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
                  {/* From */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700 flex items-center">
                      <MapPin className="w-4 h-4 mr-2 text-primary-green" />
                      Leaving from
                    </label>
                    <input
                      type="text"
                      placeholder="Enter pickup location"
                      value={searchData.from}
                      onChange={(e) => handleSearchChange('from', e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-green focus:border-transparent transition-colors duration-200"
                    />
                  </div>

                  {/* To */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700 flex items-center">
                      <MapPin className="w-4 h-4 mr-2 text-primary-green" />
                      Going to
                    </label>
                    <input
                      type="text"
                      placeholder="Enter destination"
                      value={searchData.to}
                      onChange={(e) => handleSearchChange('to', e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-green focus:border-transparent transition-colors duration-200"
                    />
                  </div>

                  {/* Date */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700 flex items-center">
                      <Calendar className="w-4 h-4 mr-2 text-primary-green" />
                      Date
                    </label>
                    <input
                      type="date"
                      value={searchData.date}
                      onChange={(e) => handleSearchChange('date', e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-green focus:border-transparent transition-colors duration-200"
                    />
                  </div>

                  {/* Search Button */}
                  <div>
                    <button
                      type="submit"
                      className="w-full bg-primary-green hover:bg-green-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200 flex items-center justify-center"
                    >
                      <Search className="w-5 h-5 mr-2" />
                      Search
                    </button>
                  </div>
                </div>
              </form>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-12">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                viewport={{ once: true }}
                className="text-center"
              >
                <div className="w-20 h-20 bg-primary-green rounded-full flex items-center justify-center mx-auto mb-6">
                  <div className="text-white">
                    {feature.icon}
                  </div>
                </div>
                <h3 className="text-2xl font-bold text-gray-900 mb-4">
                  {feature.title}
                </h3>
                <p className="text-lg text-gray-600 leading-relaxed">
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.5 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.8, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="text-center"
              >
                <div className="w-16 h-16 bg-primary-green rounded-full flex items-center justify-center mx-auto mb-4">
                  <div className="text-white">
                    {stat.icon}
                  </div>
                </div>
                <div className="text-3xl md:text-4xl font-bold text-primary-green mb-2">
                  {stat.number}
                </div>
                <div className="text-gray-600 font-medium">
                  {stat.label}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="space-y-6"
          >
            <h2 className="text-4xl md:text-5xl font-bold text-gray-900">
              Ready to Get Started?
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              Join thousands of businesses that trust TranspoLink for their transport needs. 
              Get started today and experience the difference.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/post-goods" className="btn-secondary text-lg px-8 py-4 flex items-center justify-center">
                Post Your Goods
                <ArrowRight className="w-5 h-5 ml-2" />
              </Link>
              <Link to="/trucks" className="btn-outline text-lg px-8 py-4">
                Find Available Trucks
              </Link>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
